<script>
export default {
  data() {
    return {
      shoInfo: true,
      activeStorey: null,
      selected: false,
      storeysMap: new Map(),
      entrances: {
        id: 1,
        number: "1",
        compass: 0,
        deadline_quarter: null,
        deadline_year: null,
        flats_counters: {
          booked: 0,
          free: 0,
          sold: 192,
          total: 192,
        },
        image_maps: [],
        storeys: {
          2: 1,
          3: 2,
          4: 3,
          5: 4,
          6: 5,
          7: 6,
          8: 7,
          9: 8,
          10: 9,
          11: 10,
          12: 11,
          13: 12,
          14: 13,
          15: 14,
          16: 15,
          17: 16,
          18: 17,
          19: 18,
          20: 19,
          21: 20,
          22: 21,
          23: 22,
          24: 23,
          25: 24,
        },
        house: 1,
      },
      entrance: null,
    };
  },
  created() {
    this.getEntrance();
  },
  methods: {
    // onClick() {
    //   if (!this.selected) return (this.selected = true);
    //   this.$router.push({ name: "facad.house" });
    // },
    async getEntrance() {
      this.entrance = await this.$store.dispatch("entrances/entrancesOne", {
        id: 3,
      });
    },
    hideInfo() {
      this.shoInfo = false;
      const svg = this.$refs.liter2;
      if (svg) {
        console.log(svg);
        svg.scrollIntoView({
          behavior: "smooth",
          inline: "center",
        });
      }
    },
    onClick(strIndx) {
      const { id } = this.storeys.find((_, indx) => indx === strIndx)
      if (id) {
        
        if (this.activeStorey?.id !== id) {
          return this.enter(strIndx);
        }
        
        this.$router.push({ name: "storey", params: { id: id } });
      }
    },
    async enter(strIndx) {
      const {id} = this.storeys.find((_, indx) => indx === strIndx)
      
      if (id) {
        
        if (this.storeysMap.has(id)) {
          this.activeStorey = this.storeysMap.get(id);
        } else {
          this.activeStorey = await this.$store.dispatch("storeys/storeysOne", {
            id: id,
          });
          this.storeysMap.set(id, this.activeStorey);
        }
      }
    },

    async onEnter(storeyId) {
      this.hoverStorey = { id: storeyId };

      if (this.storeysMap.has(storeyId)) {
        this.activeStorey = this.storeysMap.get(storeyId);
      } else {
        this.activeStorey = await this.$store.dispatch("storeys/storeysOne", {
          id: storeyId,
        });
        this.storeysMap.set(storeyId, this.activeStorey);
      }
    },

    leave(id) {
      if (this.timer) clearTimeout(this.timer);
      this.timer = setTimeout(() => {
        if (id === this.activeStorey?.id) {
          this.activeStorey = null;
        }
      }, 500);
    },
  },
   computed: {
    storeys() {
      if (!this.entrance) return [];
      const entries = Object.entries(this.entrance.storeys);
      const sorted = entries.sort((a, b) => b[0] - a[0]);
      return sorted.map((entry) => ({ id: entry[1], number: entry[0] }));
    },
  },
  mounted() {},
};
</script>

